const { ObjectId } = mongoose.Types;

const sanitizeOption = {
  allowedTags: [
    "h1",
    "h2",
    "b",
    "i",
    "u",
    "s",
    "p",
    "ul",
    "li",
    "ol",
    "blockquote",
    "a",
    "img",
  ],
  allowedAttributes: {
    a: ["href", "name", "target"],
    img: ["src"],
    li: ["class"],
  },
  allowedSchemes: ["data", "http"],
};

const removeHtmlAndShorten = (body) => {
  const filtered = sanitizeHtml(body, {
    allowedTags: [],
  });
  return filtered.length < 200 ? filtered : `${filtered.slice(0, 200)}...`;
};

// Posting Id로 해당 포스팅 찾게 해주는 미들웨어
// 이 미들웨어가 적용되지 않은건 아닌지 확인.
       const getPostById = async (ctx, next) => {
  const { id } = ctx.params;

  if (!ObjectId.isValid(id)) {
    ctx.status = 400; //Bad Request
    return;
  }

  try {
    const post = await Post.findById(id);
    if (!post) {
      ctx.status = 404; // Not Found
      return;
    }
    ctx.state.post = post;
    return next();
  } catch (e) {
    ctx.throw(500, e);
  }
};

       const checkOwnPost = (ctx, next) => {
  const { user, post } = ctx.state;
  if (post.user._id.toString() !== user._id) {
    ctx.status = 403;
    return;
  }
  return next();
};

/*
POST /api/posts
{
    title : '제목',
    body : '내용',
    tags : ['태그1','태그2']
}
로그인하지 않으면 write할 수 없음.
로그인 하지 않으면 401(unauthorized)
*/

       const write = async (ctx) => {
  const schema = Joi.object().keys({
    title: Joi.string().required().min(2), //최소 2글자이상 입력해야함.
    body: Joi.string().required().min(2), //최소 2글자이상 입력해야함.
    tags: Joi.array().items(Joi.string().min(2)).required(), //최소 2글자이상 입력해야함.
  });

  const result = schema.validate(ctx.request.body);

  if (result.error) {
    ctx.status = 400;
    ctx.body = result.error;
    return;
  }

  const { title, body, tags } = ctx.request.body;
  const post = new Post({
    title,
    body: sanitizeHtml(body, sanitizeOption),
    tags,
    user: ctx.state.user,
  }); // 인스턴스
  try {
    await post.save();
    // 인스턴스 데이터베이스에 저장
    // 해당 함수의 반환값은 Promise여서 async / await사용
    // 그리하여 데이터베이스에 저장할 때까지 기다릴 수 있게 await사용
    ctx.body = post;
  } catch (e) {
    ctx.throw(500, e);
  }
};

/*
GET /api/posts
FrontEnd <PostListPage/> path='/'
*/
       const list = async (ctx) => {
  try {
    const posts = await Post.find()
      .sort({ _id: -1 }) // 최신 포스팅부터 불러오게 하기 위해
      .lean()
      .exec();

    ctx.body = posts.map((post) => ({
      ...post,
      body: removeHtmlAndShorten(post.body),
    }));
  } catch (e) {
    ctx.throw(500, e);
  }
};

